import React, { useState, useEffect } from 'react';

const levels = {
  easy: 5,
  medium: 3,
  hard: 2
};

const words = [
  'grid', 'swift', 'rails', 'ruby', 'python', 'java', 'tech', 'clear', 'echo', 'let',
  'wall', 'laughter', 'hash', 'kotlin', 'mobile', 'android', 'javascript', 'web', 'program',
  'coding', 'basic', 'foodie', 'work', 'case', 'react', 'dragon', 'rush', 'api', 'virtual',
  'nerd', 'google', 'float', 'docker', 'block', 'rank', 'class', 'machine', 'perfect',
  'deploy', 'terminal', 'array', 'vue', 'node', 'issue', 'front', 'grid', 'geek', 'mac',
  'console', 'clone', 'heroku', 'slack', 'version', 'control', 'data', 'npm', 'developer'
];

function Game() {
  const [currentLevel, setCurrentLevel] = useState(levels.easy);
  const [time, setTime] = useState(levels.easy);
  const [score, setScore] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentWord, setCurrentWord] = useState('');
  const [wordInput, setWordInput] = useState('');
  const [message, setMessage] = useState('');
  const [highScore, setHighScore] = useState(localStorage.getItem('highScore') || 0);
  const [showGameOverModal, setShowGameOverModal] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [username, setUsername] = useState(localStorage.getItem('username') || '');

  useEffect(() => {
    if (username) {
      document.getElementById('username-display').innerText = `Welcome, ${username}!`;
    } else {
      window.location.href = 'register.html';
    }
  }, [username]);

  useEffect(() => {
    if (isPlaying) {
      const timer = setInterval(() => {
        setTime(prevTime => {
          if (prevTime <= 1) {
            clearInterval(timer);
            endGame();
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [isPlaying]);

  useEffect(() => {
    generateWord();
  }, [currentLevel]);

  function generateWord() {
    const randomIndex = Math.floor(Math.random() * words.length);
    setCurrentWord(words[randomIndex]);
  }

  function checkWord() {
    if (wordInput === currentWord) {
      setScore(prevScore => prevScore + 1);
      setWordInput('');
      generateWord();
      setTime(currentLevel);
      setMessage('Good Job!');

      const highScore = localStorage.getItem('highScore') || 0;
      if (score + 1 > highScore) {
        localStorage.setItem('highScore', score + 1);
        setHighScore(score + 1);
      }
    }
  }

  function setLevel(level) {
    setCurrentLevel(levels[level]);
    setTime(levels[level]);
    setScore(0);
    setIsPlaying(true);
  }

  function endGame() {
    setIsPlaying(false);
    setMessage('Game Over!');
    setFinalScore(score);
    updateHighScores();
    setShowGameOverModal(true);
  }

  function updateHighScores() {
    const highScores = JSON.parse(localStorage.getItem('highScores')) || [];
    if (username) {
      const existingUserIndex = highScores.findIndex(entry => entry.username === username);
      if (existingUserIndex >= 0) {
        if (highScores[existingUserIndex].score < score) {
          highScores[existingUserIndex].score = score;
        }
      } else {
        highScores.push({ username: username, score: score });
      }

      highScores.sort((a, b) => b.score - a.score);
      localStorage.setItem('highScores', JSON.stringify(highScores));
    }
  }

  function startAgain() {
    setShowGameOverModal(false);
    setLevel('easy'); // Default to easy level for restarting
  }

  function closeGameOverModal() {
    setShowGameOverModal(false);
  }

  function showModal() {
    const scoreSubmittedModal = document.createElement('div');
    scoreSubmittedModal.className = 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-blue-800 p-6 rounded-lg shadow-lg text-white text-center';

    const message = document.createElement('h3');
    message.innerHTML = 'Score Submitted!';
    scoreSubmittedModal.appendChild(message);

    const checkLeaderboardButton = document.createElement('button');
    checkLeaderboardButton.innerHTML = 'Check Leaderboard';
    checkLeaderboardButton.className = 'mt-4 bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-500';
    checkLeaderboardButton.onclick = function () {
      window.location.href = 'leaderboard.html'; // Redirect to leaderboard page
    };
    scoreSubmittedModal.appendChild(checkLeaderboardButton);

    const closeButton = document.createElement('button');
    closeButton.innerHTML = 'Close';
    closeButton.className = 'mt-4 bg-red-600 text-white py-2 px-4 rounded hover:bg-red-500';
    closeButton.onclick = function () {
      document.body.removeChild(scoreSubmittedModal);
    };
    scoreSubmittedModal.appendChild(closeButton);

    document.body.appendChild(scoreSubmittedModal);
  }

  function closeModal() {
    const modal = document.querySelector('#usernameModal');
    if (modal) {
      modal.classList.add('hidden');
    }
  }

  function submitUsername() {
    const usernameInput = document.querySelector('#usernameInput').value;
    if (usernameInput.trim() !== '') {
      localStorage.setItem('username', usernameInput);
      setUsername(usernameInput);
      closeModal();
    }
  }

  function logout() {
    localStorage.removeItem('username');
    window.location.href = 'register.html';
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-blue-500 text-white p-6">
      <header className="text-center mb-6">
        <div id="username-display" className="text-xl font-semibold">
          {username && `Welcome, ${username}!`}
        </div>
        <div id="level" className="text-lg">
          Current Level: <span id="current-level" className="font-bold">{Object.keys(levels).find(key => levels[key] === currentLevel)}</span>
        </div>
      </header>
      <div className="flex gap-4 mb-4">
        <button onClick={() => setLevel('easy')} className="bg-green-500 hover:bg-green-400 text-white py-2 px-4 rounded">Easy 🐷</button>
        <button onClick={() => setLevel('medium')} className="bg-yellow-500 hover:bg-yellow-400 text-white py-2 px-4 rounded">Medium 🐼</button>
        <button onClick={() => setLevel('hard')} className="bg-red-500 hover:bg-red-400 text-white py-2 px-4 rounded">Hard 🔥</button>
      </div>
      <p className="text-lg mb-2">Type the given word within <span className="font-bold">{currentLevel}</span> Seconds:</p>
      <h2 id="current-word" className="text-4xl font-bold mb-4">{currentWord}</h2>
      <h4 id="message" className="text-lg mb-4">{message}</h4>
      <input
        type="text"
        className="border border-gray-300 p-2 rounded-lg w-full max-w-md mb-4"
        placeholder="Start typing..."
        id="word-input"
        value={wordInput}
        onChange={(e) => setWordInput(e.target.value)}
        onKeyUp={checkWord}
        disabled={!isPlaying}
      />
      <hr className="my-4 w-full max-w-md border-gray-300" />
      <div className="flex justify-between w-full max-w-md mb-4">
        <h3>Time Left: <span id="time">{time}</span></h3>
        <h3>Score: <span id="score">{score}</span></h3>
      </div>
      <h4 className="text-lg mb-4">High score: <span id="high-score">{highScore}</span></h4>
      <input
        type="text"
        placeholder="Pro Tip: Sometimes it's faster to delete all and just retype!"
        id="input2"
        className="border border-gray-300 p-2 rounded-lg w-full max-w-md mb-4"
        disabled
      />
      <button onClick={logout} className="bg-red-500 hover:bg-red-400 text-white py-2 px-4 rounded">Logout</button>

      {showGameOverModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <h2 className="text-2xl font-bold mb-4">Game Over!</h2>
            <p className="mb-4">Final Score: {finalScore}</p>
            <button onClick={startAgain} className="bg-blue-500 hover:bg-blue-400 text-white py-2 px-4 rounded mr-2">Play Again</button>
            <button onClick={closeGameOverModal} className="bg-gray-500 hover:bg-gray-400 text-white py-2 px-4 rounded">Close</button>
          </div>
        </div>
      )}

      <div id="usernameModal" className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center hidden z-50">
        <div className="bg-white p-6 rounded-lg shadow-lg text-center">
          <h2 className="text-2xl font-bold mb-4">Enter your Username</h2>
          <input
            type="text"
            id="usernameInput"
            className="border border-gray-300 p-2 rounded-lg w-full mb-4"
            placeholder="Username"
          />
          <button onClick={submitUsername} className="bg-blue-500 hover:bg-blue-400 text-white py-2 px-4 rounded mr-2">Submit</button>
          <button onClick={closeModal} className="bg-gray-500 hover:bg-gray-400 text-white py-2 px-4 rounded">Close</button>
        </div>
      </div>
    </div>
  );
}

export default Game;
